# Prase website for metadata
def findmetadata(soup, data, URL):
	tempMetaData = {
		'title': '',
		'subtitle': '',
		'overview': '',
		'servings': 0,
		'calories': 0,
		'cookTime': 0
	}
	# Set website
	tempMetaData['URL'] = URL
	# Find main title 
	tempMetaData['title'] = soup.find('h1', { 'class' : 'main-title' }).text.replace('\n', '')
	# Find subtitle
	tempMetaData['subtitle'] = soup.find('h2', { 'class' : 'sub-title'}).text.replace('\n', '')
	# Find overview/description
	tempMetaData['overview'] = soup.find('p', { 'itemprop' : 'description'}).text
	
	#Find servings
	try: 
		tempServings = soup.find('span', { 'itemprop' : 'recipeYield'}).text
		# Parses the text, pulls out integers, outputs array
		tempServings = [int(s) for s in tempServings.split() if s.isdigit()]
		tempMetaData['servings'] = tempServings[0]
	except: 
		#If can't find servings, set to -1, future code will tell Echo what to say
		tempMetaData['servings'] = -1

	# Blue Apron stores integer for calories in its own span
	try: 
		tempCalories = int(soup.find('span', { 'itemprop' : 'calories'}).text)
		tempMetaData['calories'] = tempCalories
	except: 
		#If can't find calories, set to -1. Future code will tell Echo what to say
		tempMetaData['calories'] = -1

	# Cook Time is stored under the third paragraph of the rec-description-area
	tempCookTime = soup.find('div', { 'class' : 'rec-description-area'})
	try: 
		tempCookTime = tempCookTime.findAll('p')[2].text
		# Parse Cook Time for just the integers and spit out array
		tempCookTime = [int(s) for s in tempCookTime.split() if s.isdigit()]
		# Set cook time to the maximum cooktime if the cooktime is found
		try: 
			tempMetaData['cookTime'] = max(tempCookTime)
		except: 
			#If CookTime not found, then future code will tell Echo what to say
			tempMetaData['cookTime'] = -1 
	except:
		tempMetaData['cookTime'] = -1
	#Append struct to data
	data['metadata'] = tempMetaData


# Find ingredient name
def findIngredientData (soup, data):
	dataIngredients = soup.findAll('li', itemprop='ingredients')
	for i in range(0, len(dataIngredients)):
		# defin
		tempIngStruct = {
			'name' : '',
			'quantity' : 0,
			'dimension': ''
		}

		# Grab quantity from page 
		tempQuantity = dataIngredients[i].find('span', {'class' : 'amount'}).text
		# Add quantity of ingredient as integer if possible
		try:
			int(tempQuantity)
			tempIngStruct['quantity'] = float ("%.2f" % float(tempQuantity))
		# If not, this means the number is a fraction in unicode, so convert it to float before storing
		except: 
			# Case where it's only unicodedata
			try:
				tempQuantity = unicodedata.numeric(tempQuantity)
				tempIngStruct['quantity'] = float("%.2f" % float(tempQuantity))
			# Case where it's unicode data and string (1 1/2)
			except: 
				#This is hardcoded to grab first number that's a string, save it, convert the unicode, then add it back
				# This will not work if 12 1/2 items are needed. This might be an edge case
				#Grab first number (non unicode), store it and remove it 
				try: 
					firstNumberStore = float("%.2f" % float(tempQuantity[0]))
					tempQuantity = tempQuantity.replace(tempQuantity[0], '')
					tempQuantity = unicodedata.numeric(tempQuantity)
					tempIngStruct['quantity'] = firstNumberStore + tempQuantity
				# In this case, it's likely that the food is something like 4 to 6 ounces. We want to grab the lower number
				except: 
					tempQuantity = [int(s) for s in tempQuantity.split() if s.isdigit()]
					tempQuantity = tempQuantity[0]

		#TODO: Refactor to combine the way it finds the dimensions for linked vs non-linked items
		if dataIngredients[i].find('a'):
			# Add name of ingredient
			#ingredientName = dataIngredients[i]('a')[0]['data-ingredient']
			ingredientName = dataIngredients[i]('a')[0].contents[2].replace('\n', '')
			tempIngStruct['name'] = ingredientName

			dimensionsList = ["Cups", "Cup", "Tablespoons", "Tablespoon", "Teaspoons", "Teaspoon", "Pounds", "Pound", "Ounces", "Bunches", "Bunch", "Head", "Cloves", "Clove"]
			dimensionFound = False
			for dimension in dimensionsList: 
				if dimension in ingredientName:
					dimensionFound = True
					break
			if dimensionFound == True:
				ingredientName = ingredientName.replace(dimension , "")
				tempIngStruct['dimension'] = dimension
				tempIngStruct['name'] = ingredientName.lstrip()
			#If none of the above dimensions were found, let's assume there's no dimension,
			# and add a generic "Count to the dimension along with ingredientName as is
			else: 
				tempIngStruct['dimension'] = "Count"
				tempIngStruct['name'] = ingredientName
			data['ingredients'].append(tempIngStruct)

		# If it's a non-linked ingredient
		else:
			ingredientName = dataIngredients[i]('div')[0].contents[2].replace('\n', '')
			# Test for a number of different measurements in ingredientName
			dimensionsList = ["Cups", "Cup", "Tablespoons", "Tablespoon", "Teaspoons", "Teaspoon", "Pounds", "Pound", "Ounces", "Bunches", "Bunch", "Head", "Cloves", "Clove"]
			dimensionFound = False
			for dimension in dimensionsList: 
				if dimension in ingredientName:
					ingredientName = ingredientName.replace(dimension , "")
					tempIngStruct['dimension'] = dimension
					# Remove leading white space and tabs
					tempIngStruct['name'] = ingredientName.lstrip()
					dimensionFound = True
					break
			if dimensionFound == False:
				#If none of the above dimensions were found, let's assume there's no dimension,
				# and add a generic "Count to the dimension along with ingredientName as is
				tempIngStruct['dimension'] = "Count"
				tempIngStruct['name'] = ingredientName

			data['ingredients'].append(tempIngStruct)

def findSteps (soup , data): 
	ingredientsDiv = soup.findAll('div', itemprop='recipeInstructions')
	# On Blue Apron, the instructions are repeated twice. Cut the second half out of list
	ingredientsDiv = ingredientsDiv[:len(ingredientsDiv)/2]
	for step in ingredientsDiv:
		tempSteps = {
		'index' : 0,
		'title' : 'Step Title',
		'text' : 'Text of step'
		}
		# Set the index to the Step # from site
		tempSteps['index'] = int(step.find('span', {'class' : 'instr-num'}).text)
		# Grab title, replace all new line entries
		tempSteps['title'] = step.find('span', {'class' : 'instr-title'}).text.replace('\n', '')
		# Grab the instruction text
		tempSteps['text'] = unicodedata.normalize("NFKD", step.find('div', {'class' : 'instr-txt'}).text.replace('\n', ''))
		#tempSteps['text'] = unicodedata.normalize("NFKD", tempSteps['text'])
		#Append this step to the overall data structure
		data['steps'].append(tempSteps)


